mapper.delete:
DELETE 
FROM t_follow
WHERE master_uid = #{masterUid} AND fan_uid = #{fanUid}

mapper.select:
SELECT COUNT(*)
FROM t_follow
WHERE fan_uid = #{fanUid}

SELECT COUNT(*)
FROM t_follow
WHERE master_uid = #{masterUid}

SELECT master_uid
FROM t_follow
WHERE fan_uid = #{fanUid}

SELECT fan_uid
FROM t_follow
WHERE master_uid = #{masterUid}

SELECT COUNT(*) > 0
FROM t_follow
WHERE master_uid = #{masterUid} AND fan_uid = #{fanUid}
